mapper.insert:
INSERT INTO tb_member (

) VALUES (
      #{id}
    , #{loginId}
    , #{password}
    , #{name}
    , #{gender}
    , #{birthday}
    , #{phone}
    , 0
    , #{mainAddress}    /* 메인주소 */
    , #{detailAddress}  /* 디테일주소 */
    , #{extraAddress}   /* 엑스트라주소 */
    , 0     /* 멤버타입 */
    , NOW()
    , NULL
)

mapper.update:
UPDATE tb_member
SET
      modified_date = NOW()
    , name = #{name}
    , gender = #{gender}
    , birthday = #{birthday}
    , phone = #{phone}

    , password = #{password}

    , main_address = #{mainAddress}
    , detail_address = #{detailAddress}
    , extra_address = #{extraAddress}
WHERE
    id = #{id}
